                                                            htmlFor={'gender'}
                                                            label="Gender"
                                                            options={[
                                                                { name: 'Male', value: Gender.M },
                                                                { name: 'Female', value: Gender.F },
                                                                { name: 'Other', value: Gender.U }
                                                            ]}
                                                        />
                                                    )}
                                                />
                                            </Grid>
                                            <Grid col={8}>
                                                <Controller
                                                    control={control}
                                                    name="dob"
                                                    render={({ field: { onChange } }) => (
                                                        <DatePickerInput
                                                            onChange={onChange}
                                                            name="dob"
                                                            htmlFor={'dob'}
                                                            label="Date Of Birth"
                                                        />
                                                    )}
                                                />
                                            </Grid>
                                        </Grid>
                                    </Grid>
                                    {/* <Grid col={6}>
                                        <Controller
                                            control={control}
                                            name="city"
