                                                    defaultValue={value}
                                                    type="text"
                                                    label="City"
                                                    name="city"
                                                    htmlFor="city"
                                                    id="city"
                                                />
                                            )}
                                        />
                                    </Grid>
                                    <Grid col={6}>
                                        <Controller
                                            control={control}
                                            name="state"
                                            render={({ field: { onChange } }) => (
                                                <SelectInput
                                                    onChange={onChange}
                                                    name="state"
                                                    htmlFor={'state'}
                                                    label="State"
                                                    options={stateList}
                                                />
                                            )}
                                        />
                                    </Grid>
                                    <Grid col={3}>
                                        <Controller
                                            control={control}
                                            name="zip"
                                            render={({ field: { onChange, value } }) => (
                                                <Input
                                                    onChange={onChange}
                                                    defaultValue={value}
                                                    type="text"
                                                    label="Zip"
                                                    name="zip"
                                                    htmlFor="zip"
                                                    id="zip"
                                                />
                                            )}
                                        />
                                    </Grid> */}
                                    <Grid col={6}>
                                        <Controller
                                            control={control}
                                            name="patientId"
                                            render={({ field: { onChange, value } }) => (
                                                <Input
                                                    onChange={onChange}
                                                    defaultValue={value}
                                                    type="text"
                                                    label="Patient ID"
                                                    name="patientId"
                                                    htmlFor="patientId"
                                                    id="patientId"
                                                />
                                            )}
                                        />
                                    </Grid>
                                    <Grid col={12} className="flex-align-self-end">
                                        <div className="grid-row flex-justify-end flex-align-end flex-wrap">
                                            <p className="margin-right-105 text-primary text-bold margin-bottom-05">
                                                Advanced Search
                                            </p>
                                            <Button
                                                type={'button'}
                                                onClick={() =>
                                                    reset({
                                                        lastName: '',
                                                        firstName: '',
                                                        city: '',
                                                        state: '',
                                                        zip: '',
                                                        patientId: ''
                                                    })
                                                }
                                                outline>
                                                Clear
                                            </Button>
                                            <Button type={'submit'}>Search</Button>
                                        </div>
                                    </Grid>
                                </Grid>
                            </Form>
                        </Grid>
                    </Grid>
                </Grid>

                {data?.findPatientsByFilter && data?.findPatientsByFilter.length > 0 && (
                    <Grid desktop={{ col: 10 }} tablet={{ col: true }} className="bg-white margin-top-3 radius-md">
                        <Grid row className="flex-justify-center">
                            <Grid col={12} className="padding-4 border-bottom border-base-lightest">
                                <div className="grid-row flex-justify flex-align-center flex-wrap">
                                    <h2 className="font-ui-xl margin-top-0 margin-bottom-0">Search Results</h2>
                                    <div>
                                        <Button type={'button'} outline>
                                            Sort By
                                        </Button>
                                        <Button type={'button'} outline>
                                            Export Results
                                        </Button>
                                        <Button type={'button'}>Create New</Button>
                                    </div>
                                </div>
                            </Grid>
                            <Grid col={12} className="padding-4 table-checkbox">
                                <Table bordered={false} fullWidth>
                                    <TableContent tableHead={tableHead} tableBody={data?.findPatientsByFilter} />
                                </Table>
                            </Grid>
                        </Grid>
                    </Grid>
                )}
